import { Box, Badge } from '@chakra-ui/react';
import { Link } from 'react-router-dom';


export default function MovieList() {

    const slugify = str =>
        str
        .toLowerCase()
        .replace(/ /g, '-')
        .replace(/[^\w-]+/g, '');
    

    return (
        <Box maxW={'sm'} borderWidth='1px' borderRadius={'lg'} overflow='hidden' m={15}>
            <Link to={slugify}>
            </Link>
        </Box>
    )
}
