import { ThemeProvider } from 'styled-components';
import { lighTheme, darkTheme, GlobalStyle } from './theme';
import { ChakraProvider, Box, Grid, GridItem, VStack, Text, Link, Flex, HStack } from '@chakra-ui/react';
import { useState, useEffect } from 'react';
import {BrowserRouter} from "react-router-dom";
i

function App() {

  const[theme, setTheme] = useState("light");

  return (
    <ChakraProvider>
      <ThemeProvider theme={theme === 'light' ? lighTheme : darkTheme}>
        <BrowserRouter>
          <GlobalStyle/>
          <Box p={4}>
            <Flex h={16} alignItems={'center'} justifyContent={'space-between'}>
              <HStack spacing={16} alignItems={'left'}>
                <HStack as={'nav'}
                        spacing={6}
                        display={{ base: 'none', md: 'flex'}}>
                  <Link to="/">
                    Home
                  </Link>
                  <Link to="/contact">
                    Contact
                  </Link>
                </HStack>
                <Search2Icon></Search2Icon>
              </HStack>
            </Flex>
          </Box>
        </BrowserRouter>
      </ThemeProvider>
    </ChakraProvider>

  );
}

export default App;
